import{a as e,j as t,T as r}from"./main.cc7980d7.js";import{S as s}from"./Site-7eb7ba6c.js";import"./modulepreload-polyfill-3cfb730f.js";import"./DropdownMenu-c9ddf01c.js";import"./Label-b8166eab.js";import"./cross-0b515b96.js";import"./common-1b4c5772.js";function i({imgSrc:n,imgAlt:o,align:a="right"}){return e("img",{className:`BlogImage BlogImage--${a}`,src:n,alt:o})}function l(){return e("section",{className:"BlogSection flex flex-column items-center gap-xl",children:t("div",{className:"BlogSection__content",children:[e(r,{variant:"h1",weight:"bold",children:"Welcome to Third Room: Technology Preview"}),t(r,{variant:"s1",weight:"semi-bold",children:["Introducing an open, standards-based, decentralised vision of the metaverse for the open Web, built entirely on ",e("a",{href:"https://matrix.org",children:"Matrix"}),"… without cryptocurrencies, NFTs or walled gardens."]}),e(r,{variant:"c1",children:"Hi all,"}),t(r,{variant:"c1",children:[e("a",{href:"https://matrix.org",children:"Matrix"})," has always been about more than just chat, and we are incredibly excited today to finally publicly debut our first Technology Preview of Third Room - a beautiful new web client for connecting to shared 3D (or 2D) spatial environments and applications built on Matrix."]}),e(r,{variant:"c1",children:"Third Room lets you use Matrix as the platform for a truly free and open decentralised vision of the metaverse (or however you refer to interoperable virtual worlds and spatial web applications) - built entirely on open standards via liberally licensed open source implementations, and without any dependencies on cryptocurrencies, NFTs, or data-hoarding big tech silos. In other words: Third Room will be a blank canvas for you to build open metaverse environments and applications in the spirit of the original open Web."}),t("ul",{children:[e("li",{children:e("a",{href:"#the-vision",children:"The vision"})}),e("li",{children:e("a",{href:"#how-do-you-use-it",children:"How do you use it?"})}),e("li",{children:e("a",{href:"#how-does-it-work",children:"How does it work?"})}),e("li",{children:e("a",{href:"#whats-left",children:"What's left to do?"})}),e("li",{children:e("a",{href:"#what-makes-us-unique",children:"What makes Third Room unique?"})}),e("li",{children:e("a",{href:"#built-on-matrix",children:"Built entirely on Matrix"})}),e("li",{children:e("a",{href:"#what-can-you-use-it-for",children:"What could you use it for?"})}),e("li",{children:e("a",{href:"#how-does-this-fit-in",children:"How does this fit in with the rest of Matrix?"})}),e("li",{children:e("a",{href:"#next-steps",children:"Next steps"})})]}),e(i,{align:"center",imgSrc:"/landing/Multiplayer.jpg",imgAlt:"image"}),e(r,{id:"the-vision",variant:"h2",weight:"semi-bold",children:"The vision"}),t(r,{variant:"c1",children:["We see Third Room as a logical extension to today’s Matrix clients. The starting point for a Matrix room is of course simple text chat across a decentralised set of users. From there, it’s an obvious extension to add more sophisticated collaboration features such as the ability to send files, share locations, send voice messages, embed widgets, etc. The next level up from that is to add voip calling, to upgrade rooms into voice and"," ",e("a",{href:"https://twitter.com/matrixdotorg/status/1566063359996428289",target:"_blank",rel:"noreferrer noopener",children:"video rooms"})," ","for end-to-end encrypted (E2EE) conferencing. So what if you wanted to go further: to upgrade the room to interact together in any way imaginable, building on a blank canvas for open spatial collaboration - pulling in people, apps, digital assets and bridged content in order to mix together whatever realtime collaborative semantics you want… much like the Web itself lets you go wild with HTML+JS+CSS to build your own webapps? This is our goal with Third Room."]}),e(r,{id:"how-do-you-use-it",variant:"h2",weight:"semi-bold",children:"How do you use it?"}),t(r,{variant:"c1",children:["The point of today’s tech preview is to show off the capabilities of Third Room’s engine and spark interest from the community in contributing to the project, rather than it being a mainstream-usable app. In particular, we don’t yet have persistence, custom avatars, or custom games/experiences hooked up yet. That said, there’s obviously some stuff already here for you to play with! To get going, head over to"," ",e("a",{href:"https://thirdroom.io",children:"https://thirdroom.io"})," in a modern desktop browser and log in as a guest or registered user. In the tech preview, each user gets their own lobby room to familiarise themselves with the engine - which contains links (portals) to other rooms where you can go find other people to hang out with. Hit the ",e("kbd",{children:"/"})," key to pull up the keyboard shortcuts you can use to navigate and interact with the world. You can also create your own private rooms, either picking existing glTF assets to define the scene, or uploading your own glTF. To create your own glTF assets, go wild in Blender, or come ask in"," ",e("a",{href:"https://matrix.to/#/#thirdroom-dev:matrix.org",target:"_blank",rel:"noreferrer noopener",children:"#thirdroom-dev:matrix.org"})," ","about how to use our Unity asset export pipeline."]}),e(i,{align:"center",imgSrc:"/landing/Engine.jpg",imgAlt:"image"}),e(r,{id:"how-does-it-work",variant:"h2",weight:"semi-bold",children:"How does it work?"}),t(r,{variant:"c1",children:["Third Room is built on a new browser-based engine that we’ve built called"," ",e("a",{href:"https://github.com/matrix-org/thirdroom/tree/main/src/engine",target:"_blank",rel:"noreferrer noopener",children:"Manifold"}),", which links together"," ",e("a",{href:"https://threejs.org/",target:"_blank",rel:"noreferrer noopener",children:"Three.js"}),","," ",e("a",{href:"https://github.com/NateTheGreatt/bitECS",target:"_blank",rel:"noreferrer noopener",children:"bitECS"}),","," ",e("a",{href:"https://rapier.rs/",target:"_blank",rel:"noreferrer noopener",children:"Rapier"})," ","to provide a super high-performance multi-threaded game engine for the Web - which is then hooked into Matrix via"," ",e("a",{href:"https://www.npmjs.com/package/hydrogen-view-sdk",target:"_blank",rel:"noreferrer noopener",children:"Hydrogen SDK"})," ","in order to provide the Matrix networking and chat/voip components, with the rest of the UI built in React."]}),e(r,{variant:"c1",children:"Manifold achieves a stable 60fps on a typical modern laptop - on Chrome on a M1 Macbook Pro, it renders a typical scene at 2000x1244px at 60fps, with 13.12ms of unused gametime available for every 16.67ms frame, and in Safari 16 it can manage fullscreen 3440x1440px at an easy 60fps(!). It gets this crazy performance by extensive use of lock-free data structures built with SharedArrayBuffers and Atomics - producing a proper multi-threaded, multi-core capable Web-based game engine."}),e(r,{variant:"c1",children:"In practice, we currently have three threads:"}),t("ol",{className:"BlogSection__spaced-list",children:[e("li",{children:"The Main JS thread, which handles the React UI, Audio, WebRTC and Matrix via Hydrogen (although we could run Hydrogen on a worker if we wanted, but it’s so light it hasn’t necessitated it)."}),t("li",{children:["The Render thread, which renders the visuals of the world using Three.js’s WebGLRenderer to an OffscreenCanvas, and also handles resource loading (although this could be workerised too). Firefox just landed"," ",e("a",{href:"https://developer.mozilla.org/en-US/docs/Web/API/OffscreenCanvas",target:"_blank",rel:"noreferrer noopener",children:"OffscreenCanvas"})," ","in FF 105, and Chrome already had it: for Safari and browsers without OffscreenCanvas we render on the main thread instead."]}),e("li",{children:"The Game thread, which runs the Rapier physics engine in WASM, actually executes gameplay logic, and passes renderable state into the Render thead."})]}),e(r,{variant:"c1",children:"The threads communicate together with a mix of PostMessage and a triple-buffer of SharedArrayBuffers, which use Atomics to track which buffer the Game thread should be writing into; which buffer the Render thread should be reading from; and which buffer should be written into next. This lets the game engine tick at a solid 60fps, while the render thread will run at whatever crazy refresh rate your display has."}),t(r,{variant:"c1",children:["The final piece of the puzzle is"," ",e("a",{href:"https://github.com/NateTheGreatt/bitECS",target:"_blank",rel:"noreferrer noopener",children:"bitECS"})," ","- an ultra-high performance Entity Component System library written by our very own Nate, which stores the state of all the entities in the world in JavaScript TypedArrays, maintaining a cache-efficient packed data structure accessed by all the most performance critical operations in the engine - effectively enabling WASM-style speeds without having to resort to WASM."]}),t(r,{variant:"c1",children:["You can read more about the engine architecture"," ",e("a",{href:"https://github.com/matrix-org/thirdroom/blob/main/docs/engine-architecture.md",target:"_blank",rel:"noreferrer noopener",children:"in the docs"}),", or in"," ",e("a",{href:"https://github.com/matrix-org/thirdroom/discussions/47#discussion-4033099",target:"_blank",rel:"noreferrer noopener",children:"this update"})," ","from April."]}),e(r,{variant:"c1",children:"In terms of features, Third Room Technology Preview 1 provides support for:"}),t("ul",{children:[e("li",{children:"glTF asset loading from the Matrix media repository"}),e("li",{children:"Engine’s internal scene graph maps 1-to-1 with glTF concepts providing a stable API to build on for UGC and future realtime document editing"}),e("li",{children:"mixamo-backed avatar animation"}),e("li",{children:"Physics simulation via Rapier"}),e("li",{children:"Decentralised WebRTC networking via Matrix data channels (full mesh)"}),e("li",{children:"Network data smoothing using linear (and in future cubic hermite) interpolation"}),e("li",{children:"Decentralised chat via Matrix and Hydrogen SDK"}),e("li",{children:"Spatial WebRTC audio via Matrix"}),e("li",{children:"End-to-end encrypted networking via Matrix"}),e("li",{children:"Basic moderation features (name tags, mute, ignore, kick etc)"}),e("li",{children:"Unity Export via UnityGLTF"}),t("ul",{children:[e("li",{children:"Skybox (MX_background)"}),e("li",{children:"Lightmaps (MX_lightmap)"}),e("li",{children:"Reflection probes (MX_reflection_probes)"}),e("li",{children:"Spatial Audio (KHR_audio)"}),e("li",{children:"Physics Colliders (OMI_collider)"}),e("li",{children:"Spawn Points (MX_spawn_point)"})]}),e("li",{children:"Drag and drop in-browser asset optimization pipeline via glTF-transform"}),t("ul",{children:[e("li",{children:"Resize large textures"}),e("li",{children:"Remove unused nodes"}),e("li",{children:"Basis Universal Compressed texture support (KHR_texture_basisu) "}),e("li",{children:"Instanced mesh support (EXT_mesh_gpu_instancing)"})]}),e("li",{children:"Postprocessing pipeline (currently bloom and tonemapping)"}),t("li",{children:["built-in super-speedy glTF previewer (drop and drag your .glb onto"," ",e("a",{href:"https://thirdroom.io/viewer",children:"https://thirdroom.io/viewer"}),")"]}),e("li",{children:"Three.js Improvements"}),t("ul",{children:[e("li",{children:"Bulk of 3D transformation work moved to game thread"}),e("li",{children:"Scene traversal and world matrix updates use efficient bitECS data structures"}),e("li",{children:"Shared HDR lightmaps across multiple objects including instanced meshes"}),e("li",{children:"Blended HDR reflection probes using texture arrays with instanced mesh support"})]}),t("li",{children:["3D Tiles support (via NASA’s"," ",e("a",{href:"https://github.com/NASA-AMMOS/3DTilesRendererJS",target:"_blank",rel:"noreferrer noopener",children:"3DTilesRenderer"}),") for incrementally streaming environments à la Google Earth"]}),e("li",{children:"OpenID Connect for login, registration, guest access, account management."})]}),e(r,{id:"whats-left",variant:"h2",weight:"semi-bold",children:"What's left to do?"}),e(r,{variant:"c1",children:"We’re releasing the technology preview today to show what the engine is capable of right now, and to hopefully spark interest from the community in contributing to the project (especially for technical artists!). But there’s lots of stuff left to do:"}),t("ul",{className:"BlogSection__spaced-list",children:[t("li",{children:[e("span",{className:"semi-bold",children:"Custom Avatars:"})," At the moment, everyone is shown as a mixamo Y-bot. We'll be adding in the ability to set your own rigged avatar asap, needless to say!"]}),t("li",{children:[e("span",{className:"semi-bold",children:"User Generated Content:"})," The vision is of course for Third Room to be a canvas on which anyone can script their own functionality, just like folks can write Matrix bots, bridges and widgets today. However, we haven’t hooked up the APIs yet to let users write their own apps: these will likely be sandboxed WASM executables which run in their own thread. Instead, we’re currently limited to the functionality hardcoded into the client (move, run, jump, spawn, pickup, throw, etc)."]}),t("li",{children:[e("span",{className:"semi-bold",children:"Editing:"})," We have started on a direct-manipulation editor, letting you go full Inception or Dark City and get hands on with changing the world; you can trigger it with the"," ",e("kbd",{children:"§"})," or ",e("kbd",{children:"`"}),"key. Right now it just lets you navigate the scene graph of the world and select the entities, but the next step will be to turn it into a full in-world editor."," "]}),t("li",{children:[e("span",{className:"semi-bold",children:"Persistence:"})," We don’t yet save world state back into Matrix: when a user disconnects, their state and the objects they own disappears from the world. Instead, we should be storing glTF blobs into the Matrix room (as plain old files, so that boring old Matrix bots or non-Third-Room clients can also manipulate them!) to track the edits to the world, including persisting what the users have got up to within it."]}),t("li",{children:[e("span",{className:"semi-bold",children:"VR/AR:"})," We need to flip the bit to enable WebVR / WebXR support (and then tweak the app’s UI to be usable in a 3D environment)"]}),t("li",{children:[e("span",{className:"semi-bold",children:"Mobile Web support:"})," Similarly, we need to tweak the CSS and UI to support mobile Web. Ironically the engine itself performs excellently on smartphones!"]}),t("li",{children:[e("span",{className:"semi-bold",children:"SFU VoIP:"})," Currently the WebRTC networking for VoIP and data channels operates as full mesh - meaning that each participant has to redundantly send the same traffic to every other participant. As a result, after 10-20 users, things can start to bog down. We’re"," ",e("a",{href:"https://twitter.com/matrixdotorg/status/1555620039323275264",target:"_blank",rel:"noreferrer noopener",children:"busy working"})," ","on a Selective Forwarding Unit for Matrix to help this scale to hundreds/thousands of users, but it’s not landed yet (and once we’ve got it working in Element Call, we’ll have to port it over to Hydrogen for Third Room to use)."]}),t("li",{children:[e("span",{className:"semi-bold",children:"Engine features:"})," Realtime shadows (which need to play nice with the baked lightmaps; experiment underway), smarter antialiasing (e.g. Temporal Reprojection Anti Aliasing,"," ",e("a",{href:"https://github.com/mrdoob/three.js/issues/14050",target:"_blank",rel:"noreferrer noopener",children:"if/when it lands"})," ","in Three.js), depth-of-field, animated assets, etc. etc."]})]}),e(i,{align:"center",imgSrc:"/landing/Portal.jpg",imgAlt:"image"}),e(r,{id:"what-makes-us-unique",variant:"h2",weight:"semi-bold",children:"What makes Third Room unique?"}),e(r,{variant:"c1",children:"There’s obviously been way too much hype about virtual worlds and the metaverse recently, so it’s important to spell out what makes Third Room unique."}),t("ul",{className:"BlogSection__spaced-list",children:[t("li",{children:[e("span",{className:"semi-bold",children:"Web first."})," Third Room aspires to be the real-time spatial layer of the open Web, and as such our primary target platform is of course the Web, building on the incredible advances in WebGL, WebRTC, WebXR, WebWorkers and WebGPU in recent years. The Web is finally at the point where it’s possible to build a game engine whose performance rivals native applications - and so while many roads into the metaverse seem to involve installing enormous proprietary Windows-only native apps via Steam, Third Room is available to anyone with a web browser. We’ve tested on Chrome, Firefox and Safari, but it should work on any modern up-to-date browser… including on older machines: after all, the Web is for everyone. For instance, Third Room runs fine on a 2010 Macbook Air with integrated graphics. Today’s release is desktop web only, but mobile web is not far away (and performance is great!). Obviously there’s nothing stopping people from also building Third Room compatible native clients too (same as for normal Matrix) - and we hope to see Godot, Unity and Unreal clients emerge in due course! But our roots will always be on the open Web."]}),t("li",{children:[e("span",{className:"semi-bold",children:"Open standards throughout."})," Building on Matrix means that Third Room already inherits from Matrix’s"," ",e("a",{href:"https://spec.matrix.org/",target:"_blank",rel:"noreferrer noopener",children:"open standard"})," ","and"," ",e("a",{href:"https://spec.matrix.org/proposals/",target:"_blank",rel:"noreferrer noopener",children:"open governance model"}),". However, to actually describe and model the 3D assets in Third Room, we are using"," ",e("a",{href:"https://www.khronos.org/gltf/",target:"_blank",rel:"noreferrer noopener",children:"glTF"})," ","throughout - the official royalty-free open standard 3D asset format standardised by"," ",e("a",{href:"https://www.khronos.org/",target:"_blank",rel:"noreferrer noopener",children:"The Khronos Group"}),". Similarly we are building on"," ",e("a",{href:"https://www.khronos.org/webgl",target:"_blank",rel:"noreferrer noopener",children:"WebGL"})," ","and (potentially in future)"," ",e("a",{href:"https://github.com/KhronosGroup/glXF",target:"_blank",rel:"noreferrer noopener",children:"glXF"}),", also in the aegis of The Khronos Group, as well as recent W3C standards like WebRTC, WebXR, SharedArrayBuffers, Atomics, OffscreenCanvas, WebWorkers and the rest of the modern web stack. For avatars we’re looking at"," ",e("a",{href:"https://vrm.dev",target:"_blank",rel:"noreferrer noopener",children:"VRM"}),", and for streaming 3D environments we’re using"," ",e("a",{href:"https://github.com/CesiumGS/3d-tiles",target:"_blank",rel:"noreferrer noopener",children:"3D Tiles"}),".",e("br",{}),"Now, the interesting bit is where we hit the edge of the existing standards - for instance, defining how to embed glTF into Matrix rooms, or how to extend glTF to describe spatial audio assets. We already have a basic Matrix Spec Change (",e("a",{href:"https://github.com/matrix-org/matrix-spec-proposals/pull/3815",target:"_blank",rel:"noreferrer noopener",children:"MSC3815"}),") to define how to describe a 3D worlds in a Matrix room, and we’ll specify the actual world-synchronisation network protocol (which runs over"," ",e("a",{href:"https://github.com/matrix-org/matrix-spec-proposals/pull/3401",target:"_blank",rel:"noreferrer noopener",children:"MSC3401"}),"-negotiated data channels) as an MSC once it’s stabilised (you can see an early draft"," ",e("a",{href:"https://github.com/matrix-org/thirdroom/blob/main/docs/network-protocol.md",target:"_blank",rel:"noreferrer noopener",children:"here"}),"). The expectation is that all these “Metaverse over Matrix” MSCs will end up being a separate annex to the Matrix spec, looked after by an domain-specific Matrix spec working group rather than the normal Matrix spec core team (and so avoid bloating the core Matrix spec with metaverse-specific stuff).",e("br",{}),"Meanwhile, while building Third Room we’ve found ourselves repeatedly hitting the edge of the possible in glTF itself - but luckily, much as Matrix has MSCs, glTF has Extensions to let folks experiment with new functionality… and we’re proud to announce that the Matrix.org Foundation has formally joined The Khronos Group as a non-profit participant in order to formally participate in the spec process for glTF, WebGL, OpenXR etc and contribute our extensions into the official spec. We are also participating in the"," ",e("a",{href:"http://metaverse-standards.org",target:"_blank",rel:"noreferrer noopener",children:"Metaverse Standards Forum"}),", as well as being highly active in the"," ",e("a",{href:"https://omigroup.org/",target:"_blank",rel:"noreferrer noopener",children:"Open Metaverse Interoperability Group"}),"."]}),t("li",{children:[e("span",{className:"semi-bold",children:"Open Source."})," It should be no surprise that Third Room is entirely"," ",e("a",{href:"https://github.com/matrix-org/thirdroom",target:"_blank",rel:"noreferrer noopener",children:"open source"}),", licensed under the permissive Apache 2.0 Software Licence just like the rest of Matrix’s reference implementations. The reason is that we believe that foundational software should be as liberally licensed as possible in order for it to have the highest chance of success - we want this to be something everyone can contribute to, and everyone can build on for free (both commercially and non-commercially). We do not require contributors to sign away their IP with a CLA; contributors retain ownership of their IP and share the copyright of the project with each other, protecting the project from relicensing shenanigans and cementing it as a foundation that everyone can build on. Finally, Third Room is proud to build on a set of amazing open source dependencies - most notably"," ",e("a",{href:"https://threejs.org/",target:"_blank",rel:"noreferrer noopener",children:"Three.js"})," ","to abstract WebGL,"," ",e("a",{href:"https://github.com/NateTheGreatt/bitECS",target:"_blank",rel:"noreferrer noopener",children:"bitECS"})," ","to track the world’s state,"," ",e("a",{href:"https://rapier.rs/",target:"_blank",rel:"noreferrer noopener",children:"Rapier"})," ","as a physics engine,"," ",e("a",{href:"https://www.npmjs.com/package/hydrogen-view-sdk",target:"_blank",rel:"noreferrer noopener",children:"Hydrogen SDK"})," ","to power the Matrix layer, and a little bit of React to glue it all together. We hope that people will build amazing things on top of Third Room in turn!"]}),t("li",{children:[e("span",{className:"semi-bold",children:"Free, open and equitable."})," We profoundly believe that the metaverse should be free - both free as in beer, and free as in speech. Just like the Web, you shouldn’t have to pay to participate, and it should be open to everyone, without walled gardens or silos, and you should certainly not have to pay for it with your data or metadata. We want to provide a truly equitable environment where everyone is equal to create their own content and participate without intrinsic privilege or inequality. As a result, there is no artificial scarcity in Third Room, or virtual real-estate speculation, or intrinsic currencies: just like Matrix, everyone is free to participate on a level pegging, and there are no financial incentives baked into the system. Of course, folks are welcome to sell or trade content, avatars, assets, apps - just like on the Web - and there’s nothing stopping you from using cryptocurrencies either. But the platform itself will never have financial incentives or primitives baked in."]}),t("li",{children:[e("span",{className:"semi-bold",children:"Aesthetically beautiful."})," We’re determined that Third Room’s engine should be as visually sophisticated and aesthetically beautiful as possible, and that we should seed the ecosystem with beautiful high-quality artwork and assets. The latter has been a bit tricky as we don’t have any artists contributing to the project yet, but we’ve done our best by repurposing Unity assets from"," ",e("a",{href:"https://assetstore.unity.com/packages/3d/environments/sci-fi/3d-scifi-kit-vol-3-121447",target:"_blank",rel:"noreferrer noopener",children:"Creepy Cat"})," ","and"," ",e("a",{href:"https://assetstore.unity.com/packages/3d/environments/urban/city-builder-london-214943?aid=1101lMAW&utm_source=aff",target:"_blank",rel:"noreferrer noopener",children:"ReversedInt"})," ","to get the ball rolling. Meanwhile, if you are a 3D artist who’s interested in what we’re doing here -"," ",e("a",{href:"https://matrix.to/#/#thirdroom-dev:matrix.org",target:"_blank",rel:"noreferrer noopener",children:"please get in touch"}),"!"]}),t("li",{children:[e("span",{className:"semi-bold",children:"Pancake first."})," The reality is that VR/AR headsets have not yet taken over the world, and the vast majority of mainstream users are still on boring old flat 2D “pancake” laptop and smartphone screens, and we want Third Room to be amazing for the mainstream audience. As a result, our primary platform for Third Room is unashamedly the plain old web browser as viewed on a flat screen - although we will of course be adding VR/AR support via WebXR in future."]})]}),e(i,{align:"center",imgSrc:"/landing/UI.jpg",imgAlt:"image"}),e(r,{id:"built-on-matrix",variant:"h2",weight:"semi-bold",children:"Built entirely on Matrix"}),e(r,{variant:"c1",children:"It’s worth reiterating that Third Room does not have its own server - it is powered exclusively by plain old Matrix homeservers, with no new APIs or functionality required whatsoever. As a result, anyone running their own Synapse, Dendrite, Conduit or Construct can go and point Third Room at it and jump straight in. In return, Third Room inherits a tonne of amazing features directly from Matrix:"}),t("ul",{className:"BlogSection__spaced-list",children:[t("li",{children:[e("span",{className:"semi-bold",children:"Decentralised:"})," rooms are replicated across the participating servers, with no single point of failure or control. So you get decentralised access control and network partition tolerance for free: there is no single server owning or controlling the virtual world; it is impossible to connect to someone else on a different server without sharing ownership bilaterally with them.",e("br",{}),"The one exception to this in Third Room is the physics engine: currently, objects are ‘owned’ by the account which created them, which then runs the physics simulation for that object and then pushes the results out to the other users in the room to update their world view. We are not yet running a decentralised physics simulation, sadly :) We have plans to improve this in future however."]}),t("li",{children:[e("span",{className:"semi-bold",children:"VoIP:"})," Third Room uses"," ",e("a",{href:"https://github.com/matrix-org/matrix-spec-proposals/pull/3401",target:"_blank",rel:"noreferrer noopener",children:"MSC3401"})," ","both to provide spatial audio throughout the world, and also to provide the WebRTC data channels which are used for low latency networking to synchronise world state between the participants in the room (using"," ",e("a",{href:"https://github.com/vector-im/hydrogen-web/pull/705",target:"_blank",rel:"noreferrer noopener",children:"Hydrogen SDK’s implementation"})," ","of MSC3401). Currently it’s using full-mesh peer-to-peer between the various participating clients for connectivity, meaning a limit of tens of users per room, but as soon as the"," ",e("a",{href:"https://github.com/matrix-org/waterfall",target:"_blank",rel:"noreferrer noopener",children:"SFU"})," ","is stable we’ll be able to support hundreds/thousands of users per room. It’s been incredibly rewarding to see the native Matrix calling as pioneered by"," ",e("a",{href:"https://element.io/blog/element-call-beta-2-encryption-spatial-audio-walkie-talkie-mode-and-more/",target:"_blank",rel:"noreferrer noopener",children:"Element Call"})," ","coming into its own to power Third Room."]}),t("li",{children:[e("span",{className:"semi-bold",children:"E2EE:"})," Third Room is the only end-to-end encrypted metaverse client in existence, as far as we know."]}),t("li",{children:[e("span",{className:"semi-bold",children:"Authentication:"})," Matrix is moving to natively use OpenID Connect for authentication, and Third Room is the world’s first ever OIDC-native Matrix app. It implements"," ",e("a",{href:"https://github.com/matrix-org/matrix-spec-proposals/blob/hughns/delegated-oidc-architecture/proposals/3861-delegated-oidc-architecture.md",target:"_blank",rel:"noreferrer noopener",children:"MSC3861"})," ","in order to delegate all guest access, account registration, login, reCAPTCHA, 2FA, session management etc. to your identity provider. For the thirdroom.io server, we’ve set up a Third Room branded Keycloak instance as the identity provider - but in future it will also support the lightweight Matrix Authentication Service identity provider that we’re working on. It’s been incredibly impressive to see how OIDC has accelerated Third Room’s development, and a huge relief to know that in future we will never have to implement our own auth flows ever again - either in clients or servers!"]}),t("li",{children:[e("span",{className:"semi-bold",children:"Moderation:"})," Last but not least, we inherit all the work going on to improve moderation in Matrix - particularly important in a richer virtual environment with the associated risk of toxic content. We’re launching with basic moderation features like ‘kick’, ‘ban’, ‘ignore’ and ‘mute’ - and as the more sophisticated moderation tooling lands we’ll automatically inherit too."]})]}),e(r,{id:"what-can-you-use-it-for",variant:"h2",weight:"semi-bold",children:"What could you use it for?"}),e(r,{variant:"c1",children:"The sort of use cases we’re considering here are:"}),t("ul",{children:[e("li",{children:"Virtual events - imagine if FOSDEM was hybridised between real-ULB and virtual-ULB?!"}),e("li",{children:"Telepresence - what is the ultimate way for someone remote to attend an in-person meeting?"}),e("li",{children:"Virtual socialising - simply hang out with your friends online in a 3D environment"}),e("li",{children:"Music - jam on a virtual Bosendorfer, or warble on a virtual theremin…"}),e("li",{children:"Art and Design - get building 3D or 2D models and artwork directly in Third Room!"}),e("li",{children:"Gaming - the entire spectrum of multiplayer gaming could exist in Third Room"}),e("li",{children:"Entertainment - make that awesome indie VR movie on an open platform rather than in a proprietary silo"}),e("li",{children:"Online relationships - have a sanctuary just for you"}),e("li",{children:"Disaster management - visualise earthquake zones through realtime streams of LIDAR data from drones; coordinate and visualise all the disparate emergency services which need to work together..."}),e("li",{children:"GIS applications - e.g. search & rescue operations, geophysics, flood simulation"}),e("li",{children:"Smart cities - e.g. visualise town planning, power utilisation, pollution and congestion levels"}),e("li",{children:"Healthcare - remote surgery, MRI visualisation, body analytics, epidemic visualisation…"}),e("li",{children:"Education - learn chemistry in the world’s best virtual science lab!"}),e("li",{children:"Programming - nothing says object orientation like plugging objects together like lego!"}),e("li",{children:"Engineering - look inside that nuclear reactor or jet engine and figure out what part to fix?"}),e("li",{children:"Simulated environments - visit ancient Egypt... or simply build The Matrix"}),e("li",{children:"Meteorology - see how cumulo stacks will form..."}),e("li",{children:"Transport - ...and how best to fly through them"}),e("li",{children:"Climatology - visualise the impact of global warming (and stop it)?"}),e("li",{children:"Agriculture - visualise the health of my crops?"}),e("li",{children:"Eventually, 2D or document collaboration a la Figma or Etherpad - which is after all a subset of 3D if you squint hard enough."})]}),t(r,{variant:"c1",children:["…but frankly, the canvas is infinite, much like the Web itself. The goal is literally for any real-time interactive spatial application to be able to exist in Third Room - building directly on Matrix’s existing open standard APIs for end-to-end encryption, decentralised conversations, authentication and even P2P (",e("a",{href:"https://arewep2pyet.com",target:"_blank",rel:"noreferrer noopener",children:"when ready"}),"). You can compare this with the"," ",e("a",{href:"https://github.com/matrix-org/thirdroom/discussions/20",target:"_blank",rel:"noreferrer noopener",children:"original vision"})," ","of the project at kick-off back in February to see how things have evolved!"]}),e(r,{id:"how-does-this-fit-in",variant:"h2",weight:"semi-bold",children:"How does this fit in with the rest of Matrix?"}),t(r,{variant:"c1",children:["Whenever we work on metaverse or VR for Matrix (e.g."," ",e("a",{href:"https://matrix.org/blog/2018/02/05/3-d-video-calling-with-matrix-webrtc-and-webvr-at-fosdem-2018",target:"_blank",rel:"noreferrer noopener",children:"3D video calling"}),", or our"," ",e("a",{href:"https://matrix.org/blog/2017/04/04/opening-up-cyberspace-with-matrix-and-webvr",target:"_blank",rel:"noreferrer noopener",children:"original Matrix + WebVR demo"}),") we always get a some grumpy feedback along the lines of “why are you wasting time doing VR when Element still doesn’t have multi-account?!” or whatever your favourite pet Matrix or Element deficiency is."]}),t(r,{variant:"c1",children:["The fact is that Third Room has been put together by a tiny team of just Robert (project lead, formerly of Mozilla Hubs & AltspaceVR), Nate (of bitECS fame) and Ajay (of Cinny fame) - with a bit of input from Rian and Jordan (Design), Bruno (Hydrogen) and Hugh (OIDC). On the Matrix side it’s been absolutely invaluable in driving Hydrogen SDK (which also powers things like"," ",e("a",{href:"https://element.io/blog/element-launches-chatterbox/",target:"_blank",rel:"noreferrer noopener",children:"Chatterbox"})," ","and of course Hydrogen itself) - as well as helping drive native Matrix VoIP and MSC3401 implementation work, and critically being our poster-child guinea pig experiment for the first ever native OpenID Connect Matrix client! In terms of “why do this rather than improve Element” - the domain-specific expertise at play here simply isn’t that applicable to mainstream Element - instead there are tonnes of other people focused on improving Matrix (and Element). For instance we shipped a"," ",e("a",{href:"https://element.io/blog/an-unrecognisable-improvement-elements-new-design-is-here/",target:"_blank",rel:"noreferrer noopener",children:"massive update"})," ","to Element’s UI the other week."]}),e(r,{variant:"c1",children:"Also, it’s worth noting that at Element we’ve been showing off Third Room as it develops to a lot of our big customers - and in practice there is significant interest from many of our big public sector deployments to experiment with Third Room. We are depending on commercial interest to scale up work on Third Room - please get in touch if you’re interested in funding. Revisiting the list of use cases above should give you an idea of the range of applications that folks have in mind, and we hope that this will be an impetus to grow the project and see where it goes!"}),e(r,{id:"next-steps",variant:"h2",weight:"semi-bold",children:"Next steps"}),e(r,{variant:"c1",children:"Our next main milestone is to release a minimum viable product (MVP) version of Third Room which a mainstream audience can use, rather than the technology preview/showcase we’ve released today to developers. In practice, this primarily means sorting out persistence, editing, and UGC so that we (and the community!) can start creating proper applications on top of the engine. We’re aiming to release the MVP towards the end of the year, but meanwhile we’ll continuously deploying thirdroom.io off the main branch of the repository."}),t(r,{variant:"c1",children:["It might also be interesting to see whether Third Room can embed in Element using the same"," ",e("a",{href:"https://matrix.org/blog/2022/08/15/the-matrix-summer-special-2022#matryoshka-voip-embedding",target:"_blank",rel:"noreferrer noopener",children:"Matryoshka embedding"})," ","trick which is working so well with Element Call - after all; if you’re already in Matrix in Element (or some other widget-capable Matrix client), why shouldn’t you just be able to reach straight into Third Room? This will likely come much later."]}),t(r,{variant:"c1",children:["Finally: we desperately need 3d generalists, character artists, and environment artists to come work with us to help build out beautiful avatars, scenes, assets, and generally ensure that Third Room sets a precedent for being beautiful - and to make the most of the engine in general. If Blender is your happy place, please come find us in"," ",e("a",{href:"https://matrix.to/#/#thirdroom-dev:matrix.org",target:"_blank",rel:"noreferrer noopener",children:"#thirdroom-dev:matrix.org"})," ","to find out how to get involved."]}),e(r,{variant:"c1",children:"We live in exciting times: it will be fascinating to see how Third Room progresses, and whether it really could provide a viable alternative to the increasingly closed/centralised or NFT-focused visions of the metaverse which is emerging. But whatever happens, we hope you’re excited to finally play with it as we are!"})]})})}function f(){return e(s,{children:e(l,{})})}export{f as default};
